import{_ as e,o as a,c as n,a6 as o}from"./chunks/framework.OdZ9PIR3.js";const l=JSON.parse('{"title":"汤头歌诀 - 卷五 -涌吐之剂","description":"","frontmatter":{"head":[["link",{"rel":"canonical","href":"https://hefung.github.io/xuanxue/医/汤头歌诀/汤头歌诀(卷五)"}],["meta",{"property":"og:title","content":"汤头歌诀 - 卷五 -涌吐之剂"}],["meta",{"property":"og:url","content":"https://hefung.github.io/xuanxue/医/汤头歌诀/汤头歌诀(卷五)"}],["meta",{"property":"og:image","content":"https://hefung.github.io//xuanxue/logo.svg"}],["meta",{"property":"og:description","content":"中华传统五术书籍传世经典著作"}],["meta",{"name":"twitter:title","content":"汤头歌诀 - 卷五 -涌吐之剂"}],["meta",{"name":"twitter:image","content":"https://hefung.github.io//xuanxue/logo.svg"}],["meta",{"name":"twitter:description","content":"中华传统五术书籍传世经典著作"}],["meta",{"property":"article:published_time","content":"2020-07-21T08:17:36.000Z"}],["meta",{"property":"article:modified_time","content":"2026-02-21T16:19:15.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"BlogPosting\\",\\"headline\\":\\"汤头歌诀 - 卷五 -涌吐之剂\\",\\"inLanguage\\":\\"zh-Hans\\",\\"author\\":{\\"@type\\":\\"Person\\",\\"name\\":\\"Theo\\",\\"url\\":\\"https://hefung.github.io/xuanxue\\"},\\"publisher\\":{\\"@type\\":\\"Organization\\",\\"name\\":\\"Theo\\",\\"logo\\":{\\"@type\\":\\"ImageObject\\",\\"url\\":\\"https://hefung.github.io//xuanxue/logo.svg\\"}},\\"mainEntityOfPage\\":\\"https://hefung.github.io/xuanxue/医/汤头歌诀/汤头歌诀(卷五)\\",\\"description\\":\\"中华传统五术书籍传世经典著作\\",\\"url\\":\\"https://hefung.github.io/xuanxue/医/汤头歌诀/汤头歌诀(卷五)\\",\\"image\\":\\"https://hefung.github.io//xuanxue/logo.svg\\"}"]]},"headers":[],"relativePath":"医/汤头歌诀/汤头歌诀(卷五).md","filePath":"医/汤头歌诀/汤头歌诀(卷五).md","lastUpdated":1771690755000}'),p={name:"医/汤头歌诀/汤头歌诀(卷五).md"};function i(r,t,u,h,s,g){return a(),n("div",null,[...t[0]||(t[0]=[o('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /xuanxue/医/汤头歌诀/汤头歌诀(卷五).md for this page in Markdown format</div><h1 id="汤头歌诀-卷五-涌吐之剂" tabindex="-1">汤头歌诀 - 卷五 -涌吐之剂 <a class="header-anchor" href="#汤头歌诀-卷五-涌吐之剂" aria-label="Permalink to “汤头歌诀 - 卷五 -涌吐之剂”">​</a></h1><p>二首、附方六</p><p>1.瓜蒂散</p><p>瓜蒂散中赤小豆　　或入藜芦郁金凑</p><p>此吐实热与风痰　　虚者参芦一味勾</p><p>若吐虚烦栀豉汤　　剧痰乌附尖方透</p><p>古人尚有烧盐方　　一切积滞功能奏</p><p>2.稀涎散</p><p>稀涎皂角白矾班　　或益藜芦微吐间</p><p>风中痰升人眩仆　　当先服此通其关</p><p>通关散用细辛皂　　吹鼻得嚏保生还</p><p>汗、吐、下、和，乃治疗之四法。经曰 ∶ 在上者涌之，其高者因而越之，故古人治病，用吐法者最多。朱丹溪曰 ∶ 吐中就有发散之义。张子和曰 ∶ 诸汗法古方多有之，惟以吐发汗者，世罕知之。今人医疗，惟用汗、下、和，而吐法绝置不用，可见时师之缺略。特补涌吐一门，方药虽简，而法不可废也。若丹溪用四物、四君引吐，又治小便不通，亦用吐法，是又在用者之圆神矣。</p><p>[涌吐之剂] 瓜蒂散</p><p>（仲景）中赤小豆，（甜瓜蒂炒黄与赤小豆为末，热水或水调，量虚实服之。）或入藜芦郁金凑。（张子和去赤豆加藜芦、防风，一方去赤豆加郁金、韭汁，俱名三圣散。鹅翎探吐，并治风痰。）此吐实热与风痰，（瓜蒂吐实热，藜芦吐风痰。）虚者参芦（散）一味勾。（虚人痰壅不得服瓜蒂者，以参芦代之，或加竹沥。）若吐虚烦栀豉汤，（仲景，栀子十四枚，豉四合，治伤寒后虚烦。）剧痰乌附尖方透。（丹溪治许白云，用瓜蒂、栀子、苦参、藜芦，屡吐不透，后以浆水和乌附尖服，始得大吐。）古人尚有烧盐方，一切积滞功能奏。（烧盐热汤调服，以指探吐，治霍乱、宿食、冷痛等症。《千金》曰 ∶ 凡病宜吐，大胜用药。）</p><p>[涌吐之剂] 稀涎</p><p>（散，严用和）皂角白矾斑，〔皂角四挺（去皮弦炙），白矾一两，为末，每服五分。</p><p>白矾酸苦涌泄，能软顽痰；皂角辛咸通窍，专制风木。此专门之兵也，国中风时宜用之。〕或益藜芦微吐间。风中痰升人眩仆，当先服此通其关。（令微吐稀涎，续进他药。）通关散用细辛皂（角，为末，）吹鼻得嚏保生还。（卒中者用此吹鼻，有嚏者可治，无嚏者为肺气已绝。）</p>',18)])])}const d=e(p,[["render",i]]);export{l as __pageData,d as default};
