import{_ as e,o as p,c as a,a6 as n}from"./chunks/framework.OdZ9PIR3.js";const l=JSON.parse('{"title":"汤头歌诀 - 卷十三 -祛暑之剂","description":"","frontmatter":{"head":[["link",{"rel":"canonical","href":"https://hefung.github.io/xuanxue/医/汤头歌诀/汤头歌诀(卷十三)"}],["meta",{"property":"og:title","content":"汤头歌诀 - 卷十三 -祛暑之剂"}],["meta",{"property":"og:url","content":"https://hefung.github.io/xuanxue/医/汤头歌诀/汤头歌诀(卷十三)"}],["meta",{"property":"og:image","content":"https://hefung.github.io//xuanxue/logo.svg"}],["meta",{"property":"og:description","content":"中华传统五术书籍传世经典著作"}],["meta",{"name":"twitter:title","content":"汤头歌诀 - 卷十三 -祛暑之剂"}],["meta",{"name":"twitter:image","content":"https://hefung.github.io//xuanxue/logo.svg"}],["meta",{"name":"twitter:description","content":"中华传统五术书籍传世经典著作"}],["meta",{"property":"article:published_time","content":"2020-07-21T08:17:36.000Z"}],["meta",{"property":"article:modified_time","content":"2026-02-21T16:19:15.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"BlogPosting\\",\\"headline\\":\\"汤头歌诀 - 卷十三 -祛暑之剂\\",\\"inLanguage\\":\\"zh-Hans\\",\\"author\\":{\\"@type\\":\\"Person\\",\\"name\\":\\"Theo\\",\\"url\\":\\"https://hefung.github.io/xuanxue\\"},\\"publisher\\":{\\"@type\\":\\"Organization\\",\\"name\\":\\"Theo\\",\\"logo\\":{\\"@type\\":\\"ImageObject\\",\\"url\\":\\"https://hefung.github.io//xuanxue/logo.svg\\"}},\\"mainEntityOfPage\\":\\"https://hefung.github.io/xuanxue/医/汤头歌诀/汤头歌诀(卷十三)\\",\\"description\\":\\"中华传统五术书籍传世经典著作\\",\\"url\\":\\"https://hefung.github.io/xuanxue/医/汤头歌诀/汤头歌诀(卷十三)\\",\\"image\\":\\"https://hefung.github.io//xuanxue/logo.svg\\"}"]]},"headers":[],"relativePath":"医/汤头歌诀/汤头歌诀(卷十三).md","filePath":"医/汤头歌诀/汤头歌诀(卷十三).md","lastUpdated":1771690755000}'),o={name:"医/汤头歌诀/汤头歌诀(卷十三).md"};function i(r,t,u,h,s,g){return p(),a("div",null,[...t[0]||(t[0]=[n('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /xuanxue/医/汤头歌诀/汤头歌诀(卷十三).md for this page in Markdown format</div><h1 id="汤头歌诀-卷十三-祛暑之剂" tabindex="-1">汤头歌诀 - 卷十三 -祛暑之剂 <a class="header-anchor" href="#汤头歌诀-卷十三-祛暑之剂" aria-label="Permalink to “汤头歌诀 - 卷十三 -祛暑之剂”">​</a></h1><p>五首、附方十</p><p>1.三物香薷饮</p><p>三物香薷豆朴先　　若云热盛加黄连</p><p>或加苓草名五物　　利湿祛湿木瓜宣</p><p>再加参芪与陈术　　兼治内伤十味全</p><p>二香合入香苏饮　　仍有藿香葛传</p><p>2.清暑益气汤</p><p>清暑益气参草芪　　当归麦味青陈皮</p><p>曲柏葛根苍白术　　升麻泽泻姜枣随</p><p>3.缩脾饮</p><p>缩脾饮用清暑气　　砂仁草果乌梅暨</p><p>甘草葛根扁豆加　　吐泻烦渴温脾胃</p><p>古人治暑多用温　　暑为阴证此所谓</p><p>大顺杏仁姜桂甘　　散寒燥湿斯为贵</p><p>4.生脉散</p><p>生脉麦味与人参　　保肺清心治暑淫</p><p>气少汗多兼口渴　　病危脉绝急煎斟</p><p>5.六一散</p><p>六一滑石同甘草　　解肌行水兼清燥</p><p>统治表里及三焦　　热渴暑烦泻痢保</p><p>益元碧玉与鸡苏　　砂黛薄荷加之好</p><p>[祛暑之剂] 三物香薷</p><p>（饮，《局方》）豆朴先，（香薷辛温香散，能入脾肺，发越阳气以散蒸热。浓朴除湿散满，扁豆清暑和脾。）若云热盛加黄连。（名黄连香薷饮，《活人》治中暑热盛，口渴心烦。）或加苓草（茯苓、甘草）名五物（香薷饮），利湿祛暑木瓜宣。（加木瓜名六味香薷饮，木瓜、茯苓治湿盛。）再加参与除术，兼治内伤十味全。（六味加参、、陈皮、白术，名十味香薷饮。）二香（散）合入香苏饮，（五味香薷饮合香苏饮。香附、紫苏、陈皮、苍术，名二香散，治外感内伤，身寒腹胀。）仍有藿薷（汤）香葛（汤）传。（三物香薷饮合藿香正气散，名藿薷汤，治伏暑吐泻；三物香薷饮加葛根，名香葛汤，治暑月伤风。）</p><p>[祛暑之剂] 清暑益气</p><p>（汤，东垣）参草，当归香味青陈皮。曲柏葛根苍白术，升麻泽泻枣姜随。〔人参、黄、甘草（炙）、当归（酒洗）、麦冬、五味、青皮（麸炒）、陈皮（留白）、神曲（炒）、黄柏（酒炒）、葛根、苍术、白术（土炒）、升麻、泽泻，加姜、枣煎。热伤气，参、补气敛汗；湿伤脾，二术燥湿强脾。火旺则金病而水衰，故用麦、味保肺生津，黄柏泻火滋水，青皮理气而破滞，当归养血而和阴，曲、草和中而消食，升、葛以升清，泽泻以降浊也。〕</p><p>[祛暑之剂] 缩脾饮</p><p>用清暑气，砂仁草果乌梅暨。甘草葛根扁豆加，吐泻烦渴温脾胃。（砂仁、草果（乌梅、甘草（炙）各四两，扁豆（炒研）、葛根各二两。暑必兼温，而湿属脾土，故用砂仁、草果利气温脾，扁豆解暑渗湿，葛根升阳生津，甘草补土和中，乌梅清热止渴。）古人治暑多用温，（如香薷饮、大顺散之类。）暑为阴证此所谓。（洁古曰 ∶ 中热为阳证，为有余；中暑为阴证，为不足。经曰 ∶ 脉虚身热，得之伤暑。）大顺（散）杏仁姜桂甘，散寒燥湿斯为贵。（先将甘草、白砂仁，次入干姜、杏仁（炒），合肉桂为末，每服一钱。吴鹤皋曰 ∶ 此非治暑，乃治暑月饮冷受伤之脾胃耳。）</p><p>[祛暑之剂] 生脉散</p><p>麦味与人参，保肺清心治暑淫。气少汗多兼口渴，病危脉绝急煎斟。（人参五分八分，五味子九粒。人参大补肺气，麦冬甘寒润肺，五味酸收敛肺，并能泻火生津。盖心主脉，肺朝百脉，补肺清心，则气充而脉复。将死脉绝者服之，能令复生。夏月火旺烁金，尤宜服之。）</p><p>[祛暑之剂] 六一（散）</p><p>滑石同甘草，解肌行水兼清燥。统治表里及三焦，热渴暑烦泻痢保。（滑石六两，甘草一两，灯心汤下，亦有用姜汤下者。滑石气轻解肌，质重泻火，滑能入窍，淡能行水，故能通治上下表里之湿热，甘草泻火和中，又以缓滑石之寒滑也。）益元（散）碧玉与鸡苏（散），砂黛薄荷加之好。（前方加辰砂，名益元散，取其清心；加青黛，名碧玉散，取其凉肝；加薄荷，</p>',33)])])}const d=e(o,[["render",i]]);export{l as __pageData,d as default};
