import{_ as e,o as p,c as a,a6 as n}from"./chunks/framework.OdZ9PIR3.js";const c=JSON.parse('{"title":"类证治裁 卷五","description":"","frontmatter":{"head":[["link",{"rel":"canonical","href":"https://hefung.github.io/xuanxue/医/类证治裁/类证治裁(卷五)"}],["meta",{"property":"og:title","content":"类证治裁 卷五"}],["meta",{"property":"og:url","content":"https://hefung.github.io/xuanxue/医/类证治裁/类证治裁(卷五)"}],["meta",{"property":"og:image","content":"https://hefung.github.io//xuanxue/logo.svg"}],["meta",{"property":"og:description","content":"中华传统五术书籍传世经典著作"}],["meta",{"name":"twitter:title","content":"类证治裁 卷五"}],["meta",{"name":"twitter:image","content":"https://hefung.github.io//xuanxue/logo.svg"}],["meta",{"name":"twitter:description","content":"中华传统五术书籍传世经典著作"}],["meta",{"property":"article:published_time","content":"2020-07-21T08:17:36.000Z"}],["meta",{"property":"article:modified_time","content":"2026-02-21T16:19:15.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"BlogPosting\\",\\"headline\\":\\"类证治裁 卷五\\",\\"inLanguage\\":\\"zh-Hans\\",\\"author\\":{\\"@type\\":\\"Person\\",\\"name\\":\\"Theo\\",\\"url\\":\\"https://hefung.github.io/xuanxue\\"},\\"publisher\\":{\\"@type\\":\\"Organization\\",\\"name\\":\\"Theo\\",\\"logo\\":{\\"@type\\":\\"ImageObject\\",\\"url\\":\\"https://hefung.github.io//xuanxue/logo.svg\\"}},\\"mainEntityOfPage\\":\\"https://hefung.github.io/xuanxue/医/类证治裁/类证治裁(卷五)\\",\\"description\\":\\"中华传统五术书籍传世经典著作\\",\\"url\\":\\"https://hefung.github.io/xuanxue/医/类证治裁/类证治裁(卷五)\\",\\"image\\":\\"https://hefung.github.io//xuanxue/logo.svg\\"}"]]},"headers":[],"relativePath":"医/类证治裁/类证治裁(卷五).md","filePath":"医/类证治裁/类证治裁(卷五).md","lastUpdated":1771690755000}'),o={name:"医/类证治裁/类证治裁(卷五).md"};function i(r,t,u,h,s,g){return p(),a("div",null,[...t[0]||(t[0]=[n('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /xuanxue/医/类证治裁/类证治裁(卷五).md for this page in Markdown format</div><h1 id="类证治裁-卷五" tabindex="-1">类证治裁 卷五 <a class="header-anchor" href="#类证治裁-卷五" aria-label="Permalink to “类证治裁 卷五”">​</a></h1><h2 id="痹症" tabindex="-1">痹症 <a class="header-anchor" href="#痹症" aria-label="Permalink to “痹症”">​</a></h2><p>风寒湿合痹</p><p>出处：类证治裁--〔卷五痹症〕</p><p>名词解释：气血凝滞，身重而痛，手足挛急。（石顽改定三痹汤，或通痹散）</p><p>周痹</p><p>出处：类证治裁--〔卷五痹症〕</p><p>名词解释：真气不能周于身，浑身痹痛。（风寒湿气客于肉分，内不在脏，外未发皮，命曰周痹，蠲痹汤加桂枝、白朮、狗脊、薏米）</p><p>行痹</p><p>出处：类证治裁--〔卷五痹症〕</p><p>名词解释：身走注不定，上半身甚者，（乌药顺气散）下半身甚者。（虎骨散加减）</p><p>痛痹</p><p>出处：类证治裁--〔卷五痹症〕</p><p>名词解释：历节挛痛，（疏风活血汤）痛甚者。（五灵散）</p><p>着痹</p><p>出处：类证治裁--〔卷五痹症〕</p><p>名词解释：留着定处，身重酸疼，天阴即发，（除湿蠲痛汤加蚕沙、防己、薏米）不应。（补中益气汤加附子、羌活、黄蘗）</p><p>骨痹</p><p>出处：类证治裁--〔卷五痹症〕</p><p>名词解释：即寒痹痛痹也，苦痛切骨。（安肾丸）</p><p>筋痹</p><p>出处：类证治裁--〔卷五痹症〕</p><p>名词解释：即风痹也，风热攻注，筋弦脉缓，（羚羊角散）若湿邪入筋。（续断丹）</p><p>脉痹</p><p>出处：类证治裁--〔卷五痹症〕</p><p>名词解释：即热痹也，（金匮云，经湿则痹，络热则痿，）风湿郁热，经隧为壅。（升麻汤去桂麻，加萆薢、石膏，或秦艽四物汤，后用人参丸）</p><p>肌痹</p><p>出处：类证治裁--〔卷五痹症〕</p><p>名词解释：即湿痹着痹也，浑身上下左右麻木，属卫气不行，（神效黄汤）皮肤麻木，属肺气不行，（本方去蔓荆，倍黄耆，加防风）肌肉麻木，属营气不行，（本方去蔓荆，加桂枝、羌活、防风，丹溪曰，麻为气虚，木为湿痰败血）</p><p>皮痹</p><p>出处：类证治裁--〔卷五痹症〕</p><p>名词解释：邪在皮毛，搔如隔帛，或瘾疹风疮，宜疏风养血。（秦艽地黄汤）</p><p>五脏痹</p><p>出处：类证治裁--〔卷五痹症〕</p><p>名词解释：经病入脏，邪胜正虚，（五痹汤）肾痹，本方加独活、肉桂、杜仲、牛膝、黄耆、萆薢）肝痹，（本方加枣仁、柴胡）心痹，（本方加远志、茯神、麦冬、犀角）脾痹，（本方加厚朴、枳实、砂仁、神曲）肺痹（本方加半夏、杏仁、麻黄、紫菀）</p>',36)])])}const d=e(o,[["render",i]]);export{c as __pageData,d as default};
